import { useState, useEffect } from "react";
import { useDispatch } from "react-redux";
import { addActivity, getActivities, getCountries } from "../../Redux/actions";
import Multilist from "../Multilist/Multilist" 

const Form = () => {
    const dispatch = useDispatch()
    const [activity, setActivity] = useState({
        name: "",
        difficult: "",
        duration: "",
        season: "",
        countries: [""],
    });

    
    const handleChange = (event) => {
        setActivity({
            ...activity,
            [event.target.name]: event.target.value,
        });
    }
    
    const handleSubmit = (event) => {
        event.preventDefault();
        dispatch(addActivity(activity))
        alert("the activity has been created successfully")
        setActivity({
            name: "",
            difficult: "",
            duration: "",
            season: "",
            countries: "",
        })
    }
    
    useEffect(() => {
        dispatch(getActivities())
        dispatch(getCountries())
    }, [dispatch])

    return (
        <div>
            <form onSubmit={handleSubmit} className="form">
                <h1>Create a tourist activity</h1>
                <div>
                    <label htmlFor="name">Name: </label>
                    <input type="text" placeholder="place name" name="name" value={activity.name} onChange={handleChange} />
                </div>
                <div>
                    <label htmlFor="difficult">Difficult: </label>
                    <input type="number" placeholder="place difficult" name="difficult" value={activity.difficult} onChange={handleChange} />
                </div>
                <div>
                    <label htmlFor="duration">Duration: </label>
                    <input type="number" placeholder="place duration" name="duration" value={activity.duration} onChange={handleChange} />
                </div>
                <div>
                    <label htmlFor="season">Season: </label>
                    <input type="text" placeholder="place season" name="season" value={activity.season} onChange={handleChange} />
                </div>
                <div>
                    <label htmlFor="countries">Countries: </label>
                    <input type="text" placeholder="place countries" name="countries" value={activity.countries} onChange={handleChange} />
                    <Multilist setInFormCountry={setInFormCountry}/>
                </div>

                <button type="submit">Create</button>

            </form>            
        </div>
    )
}

export default Form;
