import Card from "../Card/Card";
import {useDispatch, useSelector} from "react-redux";
import { changePage, orderCountries } from "../../Redux/actions";
import { useState } from "react";


const Cards = () => {
    const countries = useSelector((state) => state.countries);
    const dispatch = useDispatch();

    const page = useSelector((state) => state.page);
    const itemsPerPage = 10;
    const start = (page - 1) * itemsPerPage;
    const end = start + itemsPerPage;
    const pageItems = countries.slice(start, end);

    const handleChange = (pageNumber) => {
        dispatch(changePage(pageNumber));
    }


    const [orderOption, setOrderOption] = useState('');

    const handleOrderChange = (event) => {
        const selectedOption = event.target.value;
        setOrderOption(selectedOption);
        dispatch(orderCountries(selectedOption));
    };



    return (
        <div>
            <div>
                <select value={orderOption} onChange={handleOrderChange}>
                    <option value="">Sort by</option>
                    <option value="name_asc">Name (A-Z)</option>
                    <option value="name_desc">Name (Z-A)</option>
                    <option value="population_desc">Population (major to minor)</option>
                    <option value="population_asc">Population (minor to major)</option>
                </select>
            </div>
            {
                pageItems.map(({ id, name, image, region }) => {
                    return (
                        <Card
                            key={id}
                            id={id}
                            name={name}
                            image={image}
                            region={region}
                        />
                    )
                })
            }
            <button onClick={() => handleChange(page - 1)}>ðŸ¡¸</button>
            <button onClick={() => handleChange(page + 1)}>ðŸ¡º</button>
        </div>
    )
}

export default Cards;
