import { GET_COUNTRIES, GET_COUNTRY_DETAIL, GET_COUNTRY_BY_NAME, GET_ACTIVITIES, ADD_ACTIVITY, CHANGE_PAGE, ORDER_COUNTRIES, FILTER_BY_CONTINENT } from "./actions-types";

const initialState = {
    countries: [],
    allCountries:[],
    countryDetail: {},
    activities: [],
    newActivity: "",
    page: 1,
};

const reducer = (state = initialState, { type, payload }) => {
    switch(type){
        case GET_COUNTRIES:
            return{
                ...state,
                countries: payload
            }
        case GET_COUNTRY_DETAIL:
            return{
                ...state,
                countryDetail: payload
            }
        case GET_COUNTRY_BY_NAME:
            return{
                ...state,
                countries: payload
            }
        case GET_ACTIVITIES:
            return{
                ...state,
                activities: payload  
            }
        case ADD_ACTIVITY:
            return{
                ...state,
                activity: payload
            }
        case CHANGE_PAGE:
            return{
                ...state,
                page: payload
            }
        case ORDER_COUNTRIES:
            const { orderOption } = payload;
            let orderCountries

            if (orderOption === 'name_asc') {
                orderCountries = [...state.countries].sort((a, b) =>
                a.name.localeCompare(b.name)
                );
                } else if (orderOption === 'name_desc') {
                    orderCountries = [...state.countries].sort((a, b) =>
                    b.name.localeCompare(a.name)
                    );
                } else if (orderOption === 'population_asc') {
                    orderCountries = [...state.countries].sort((a, b) => 
                    a.population - b.population
                    );
                } else if (orderOption === 'population_desc') {
                    orderCountries = [...state.countries].sort((a, b) => 
                    b.population - a.population
                    );
                } else {
                    orderCountries = [...state.countries];
                }
            return {
                ...state,
                countries: orderCountries
            };
        case FILTER_BY_CONTINENT:
            // const continent = payload
            // const filterByContinent = state.countries.filter(country => country.continent === continent);
            // return {
            //     ...state,
            //     countries: filterByContinent
            // };
            const allCountries = state.countries;
            const continent = payload
            const filterContinent = continent === 'All' ? allCountries : allCountries.filter(country => country.continent === continent)
            return {...state, countries: filterContinent}
        default:
            return{
                ...state,
            };
    };
};

export default reducer;
