import { useParams } from "react-router-dom";
import { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { getCountryDetail } from "../../Redux/actions";

const Detail = () => {
    const { id } = useParams();
    const pais = useSelector(state => state.countryDetail);
    const dispatch = useDispatch();

    useEffect(() => {
        dispatch(getCountryDetail(id));
    }, [dispatch, id]);

    console.log(pais)
    if (!pais) {
        return <p>Cargando...</p>;
      }
    return(
        <div>
            <h1>Esta es la view de DETAIL {id}</h1>
                <h2>{pais?.name}</h2>
                <img src={pais?.image} alt={pais.name}/>
                <h2>{pais?.region}</h2>
                <h2>{pais?.capital}</h2>
                <h2>{pais?.subregion}</h2>
                <h2>{pais?.area}</h2>
                <h2>{pais?.population}</h2>
 {/* <h2>{country[0].name}</h2>
                <img src={country[0].image} alt={country.name}/>
                <h2>{country[0].region}</h2>
                <h2>{country[0].capital}</h2>
                <h2>{country[0].subregion}</h2>
                <h2>{country[0].area}</h2>
                <h2>{country[0].population}</h2> */}
            {/*  <h1>Activity:</h1>
            <h2>{country?.activity.name}</h2>
            <h2>{country?.activity.difficult}</h2>
            <h2>{country?.activity.duration}</h2>
            <h2>{country?.activity.season}</h2>  */}
        </div>
    );
};

export default Detail;
