import { useLocation } from "react-router-dom"

const HandleHomeClick = () => {
    const location = useLocation();
    
    const handle = () => {
    if(location.pathname !== "/home") { 
        window.location.href = "/home"
    }else {
        window.location.reload()
    };
    }
    handle()
}

export default HandleHomeClick
