import { useParams } from "react-router-dom";
import { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { getCountryDetail } from "../../Redux/actions";

const Detail = () => {
    const dispatch = useDispatch();
    const { id } = useParams();
    const country = useSelector((state) => state.countryDetail);

    useEffect(() => {
        dispatch(getCountryDetail(id));
    }, [dispatch, id]);

    console.log(country)
    if (!country) {
        return <p>Cargando...</p>;
      }
    return(
        <div>
            <h1>Esta es la view de DETAIL {id}</h1>
                <h1>{country?.name}</h1>
                <img src={country?.image} alt={country.name}/>
                <h2>Continent: {country?.region}</h2>
                <h2>Capital: {country?.capital}</h2>
                <h2>Subregion: {country?.subregion}</h2>
                <h2>{country?.area}</h2>
                <h2>{country?.population}</h2>
 {/* <h2>{country[0].name}</h2>
                <img src={country[0].image} alt={country.name}/>
                <h2>{country[0].region}</h2>
                <h2>{country[0].capital}</h2>
                <h2>{country[0].subregion}</h2>
                <h2>{country[0].area}</h2>
                <h2>{country[0].population}</h2> */}
            {/*  <h1>Activity:</h1>
            <h2>{country?.activity.name}</h2>
            <h2>{country?.activity.difficult}</h2>
            <h2>{country?.activity.duration}</h2>
            <h2>{country?.activity.season}</h2>  */}
        </div>
    );
};

export default Detail;
