import { useState, useEffect } from "react";
import { useDispatch } from "react-redux";
import { addActivity, getActivities, getCountries } from "../../Redux/actions";

const Form = () => {
    const dispatch = useDispatch
    const [activity, setActivity] = useState({
        name: "",
        difficult: "",
        duration: "",
        season: "",
        Country: "",
    });

    
    const handleChange = (event) => {
        setActivity({
            ...activity,
            [event.target.name]: event.target.value,
        });
    }
    
    const handleSubmit = (event) => {
        event.preventDefault();
        dispatch(addActivity(activity))
    }
    
    useEffect(() => {
        dispatch(getActivities())
        dispatch(getCountries())
    }, [dispatch])

    return (
        <div>
            <form onSubmit={handleSubmit} className="form">
                <div>
                    <label htmlFor="name">Name: </label>
                    <input type="text" placeholder="place name" name="name" value={activity.name} onChange={handleChange} />
                </div>
                <div>
                    <label htmlFor="difficult">Difficult: </label>
                    <input type="text" placeholder="place difficult" name="difficult" value={activity.difficult} onChange={handleChange} />
                </div>
                <div>
                    <label htmlFor="duration">Duration: </label>
                    <input type="text" placeholder="place duration" name="duration" value={activity.duration} onChange={handleChange} />
                </div>
                <div>
                    <label htmlFor="season">Season: </label>
                    <input type="text" placeholder="place season" name="season" value={activity.season} onChange={handleChange} />
                </div>
                <div>
                    <label htmlFor="season">Season: </label>
                    <input type="text" placeholder="place season" name="season" value={activity.season} onChange={handleChange} />
                </div>
            </form>            
        </div>
    )
}

export default Form;
