import { GET_COUNTRIES, GET_COUNTRY_DETAIL, GET_COUNTRY_BY_NAME, GET_ACTIVITIES, ADD_ACTIVITY, CHANGE_PAGE, ORDER_COUNTRIES  } from "./actions-types";

const initialState = {
    countries: [],
    countryDetail: {},
    activities: [],
    newActivity: "",
    page: 1,
};

const reducer = (state = initialState, { type, payload }) => {
    switch(type){
        case GET_COUNTRIES:
            return{
                ...state,
                countries: payload
            }
        case GET_COUNTRY_DETAIL:
            return{
                ...state,
                countryDetail: payload
            }
        case GET_COUNTRY_BY_NAME:
            return{
                ...state,
            countries: payload
            }
        case GET_ACTIVITIES:
            return{
                ...state,
                activities: payload  
            }
        case ADD_ACTIVITY:
            return{
                ...state,
                activity: payload
            }
        case CHANGE_PAGE:
            return{
                ...state,
                page: payload
            }
        case ORDER_COUNTRIES:
            const { orderKey, order } = payload;
            let orderCountries

            if (order === 'name_asc') {
                orderCountries = [...state.countries].sort((a, b) =>
                  a[orderKey].localeCompare(b[orderKey])
                );
              } else if (order === 'asc') {
                orderCountries = [...state.countries].sort(
                  (a, b) => a[orderKey] - b[orderKey]
                );
              } else if (order === 'desc') {
                orderCountries = [...state.countries].sort(
                  (a, b) => b[orderKey] - a[orderKey]
                );
              } else {
                orderCountries = [...state.countries];
              }
                    
                // if(order === 'asc') {
                //     return valueA.localeCompare(valueB);
                // }else{return valueB.localeCompare(valueA);}

            return {
                ...state,
                countries: orderCountries
            };
            
        default:
            return{
                ...state,
            };
    };
};

export default reducer;
