import Card from "../Card/Card";
import {useDispatch, useSelector} from "react-redux";
import { changePage, orderCountries } from "../../Redux/actions";
import { useState } from "react";
import Select from "./Select";


const Cards = () => {
    const countries = useSelector((state) => state.countries);
    const dispatch = useDispatch();

    const page = useSelector((state) => state.page);
    const itemsPerPage = 10;
    const start = (page - 1) * itemsPerPage;
    const end = start + itemsPerPage;
    const pageItems = countries.slice(start, end);

    const handleChange = (pageNumber) => {
        dispatch(changePage(pageNumber));
    }


    const [orderKey, setOrderKey] = useState(null);
    const [order, setOrder] = useState('asc');

    const handleOrder = (key) => {
        if (key === orderKey){
        const newOrder = order === 'asc' ? 'desc' : 'asc';
        setOrder(newOrder);
        }else{
            setOrderKey(key);
            setOrder('asc');
        }

        dispatch(orderCountries(key, order))
    }

    return (
        <div>
            <div>
                <Select
                    value={orderKey}
                    onChange={(e) => handleOrder(e.target.value)}
                >
                    <option value="">Ordenar por</option>
                    <option value="population_asc">PoblaciÃ³n (mayor a menor)</option>
                    <option value="population_desc">PoblaciÃ³n (menor a mayor)</option>
                    <option value="name">Ordenar nombres alfaveticamente</option>
                </Select>
            </div>
            {/* <button onClick={() => handleOrder('name')}>Ordenar por nombre</button> */}
            <button onClick={() => handleOrder('population')}>Ordenar por population</button>
            {
                // countries?.map(({ id, name, image, region  }) => {
                pageItems.map(({ id, name, image, region, population  }) => {
                    return (
                        <Card
                            key={id}
                            id={id}
                            name={name}
                            image={image}
                            region={region}
                            population={population}
                        />
                    )
                })
            }
            <button onClick={() => handleChange(page - 1)}>ðŸ¡¸</button>
            <button onClick={() => handleChange(page + 1)}>ðŸ¡º</button>
        </div>
    )
}

export default Cards;
