import "./Paginate.css"
import { useDispatch, useSelector } from "react-redux";
import { changePage } from "../../Redux/actions"

const Paginate = () => {
    const dispatch = useDispatch();
    const page = useSelector((state) => state.page);
    const countries = useSelector((state) => state.countries);
    const totalPages = countries.length / 10;

    const handleChange = (pageNumber) => {
        dispatch(changePage(pageNumber));
    }

    return (
        <div className="paginate">
            { page > 1 ? 
                <>
                    <button onClick={() => handleChange(page - 1)}>ðŸ¡¸</button>
                    <label className="prev">{page - 1}</label>
                </> 
             : null
            }
            <label className="in">{page}</label>
            { page < totalPages ? 
                <>
                    <label className="next">{page + 1}</label>
                    <button onClick={() => handleChange(page + 1)}>ðŸ¡º</button>
                </> 
             : null
            }
        </div>
    )
}

export default Paginate
