    //         setOrder('asc');
    //     }

    //     dispatch(orderCountries(key, order))
    // }
    const handleOrderChange = (event) => {
        const selectedOption = event.target.value;
        setOrderOption(selectedOption);
        dispatch(orderCountries(selectedOption));
      };

    return (
        <div>
            <div>
                {/* <Select value={orderKey} onChange={(event) => handleOrder(event.target.value)}> */}
                <Select value={orderOption} onChange={handleOrderChange}>
                    <option value="">Ordenar por</option>
                    <option value="name_asc">Nombre (A-Z)</option>
                    <option value="name_desc">Nombre (Z-A)</option>
                    <option value="population_desc">PoblaciÃ³n (mayor a menor)</option>
                    <option value="population_asc">PoblaciÃ³n (menor a mayor)</option>
                </Select>
            </div>
            {/* <button onClick={() => handleOrder('name')}>Ordenar por nombre</button> */}
            {/* <button onClick={() => handleOrder('population')}>Ordenar por population</button> */}
            {
                // countries?.map(({ id, name, image, region  }) => {
                pageItems.map(({ id, name, image, region, population  }) => {
                    return (
                        <Card
                            key={id}
                            id={id}
                            name={name}
                            image={image}
                            region={region}
                            population={population}
                        />
                    )
                })
            }
            <button onClick={() => handleChange(page - 1)}>ðŸ¡¸</button>
            <button onClick={() => handleChange(page + 1)}>ðŸ¡º</button>
        </div>
    )
}

export default Cards;
